import React, { Component } from "react";
import DeleteBtn from "../components/DeleteBtn";
import Jumbotron from "../components/Jumbotron";
import API from "../utils/API";
import SearchResults from "../components/SearchResults";
import { Link } from "react-router-dom";
import { Col, Row, Container } from "../components/Grid";
import { List, ListItem } from "../components/List";
import { Input, FormBtn } from "../components/Form";

class SearchBooks extends Component {
  state = {
    query: "",
    books: []
  };

  handleInputChange = event => {
    this.setState({ query: event.target.value });
  };

  handleFormSubmit = event => {
    event.preventDefault();
    if (this.state.query) {
      API.getGoogleBooks(this.state.query).then(res => {
        let results = res.data.items

        results = results.map(result => {
          result = {
            key: result.id,
            id: result.id,
            title: result.volumeInfo.title,
            author: result.volumeInfo.author,
            description: result.volumeInfo.description,
            image: result.volumeInfo.imageLinks.thumbnail,
            link: result.volumeInfo.infoLink
          }
          return result;

        })
        this.setState({books: results})
      }).catch(err => console.log(err))
    }
  };

  handleSaveButton = event => {
    event.preventDefault();
    let savedBook = this.state.books.filter(
      book => book.id === event.target.id
    );
    API.saveBook(savedBook)
      .then(res => console.log("book saved!"))
      .catch(err => console.log(err));
  };

  render() {
    return (
      <>
        <Container fluid>
          <Jumbotron>
            <h1>Find books using the GoogleBooks API</h1>
          </Jumbotron>
          <Row>
            <form>
              <Input
                value={this.state.query}
                name="query"
                placeholder="Enter book title"
                onChange={this.handleInputChange}
              />
              <FormBtn
                disabled={!this.state.query}
                onClick={this.handleFormSubmit}
              >
                Search for book!
              </FormBtn>
            </form>
          </Row>
        </Container>
        <Container>
          <Row>
            <SearchResults
              books={this.state.books} handleSaveButton={this.handleSaveButton}
            />
          </Row>
        </Container>
      </>
    );
  }
}

export default SearchBooks
